import React from "react";
import { Button } from "react-materialize";
import HomeImage from "../images/home.jpg";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="home-page">
      <div className="image-container">
        <img src={HomeImage} alt="Me in Maine" />
      </div>
      <div className="text-container">
        <div>
          <h1>VIVIAN LI</h1>
          {/* bullet points? */}
          <p>Ph.D. student at the University of Colorado Boulder</p>
          <p>Advised by Aaron Clauset in the Department of Computer Science</p>
          <p>
            Co-advised by Laura Dee in the Department of Ecology & Evolutionary
            Biology
          </p>
        </div>
        <div className="footer">
          <Button
            floating
            large
            waves="light"
            icon={<i className="fa fa-envelope-open-o"></i>}
          />
          <Link to="/resume">Resume</Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
